import React from 'react'

import {
  DropdownContainer,
  DropdownItem,
  DropdownItemsContainer,
  DropdownItemsList,
  DropdownItemText,
} from './style'

export const DropdownComponent: React.FC<{
  cities: Array<[string, number, number]>
  active: boolean
}> = ({ cities, active }) => {
  const handleItemSelected = (city: string): void => {
    console.log('item selected...', city)
  }

  return (
    <DropdownContainer>
      <DropdownItemsContainer open={active}>
        <DropdownItemsList>
          {cities.map((city) => (
            <DropdownItem
              key={city[0]}
              onClick={() => handleItemSelected(city[0])}
            >
              <DropdownItemText>{city[0]}</DropdownItemText>
            </DropdownItem>
          ))}
        </DropdownItemsList>
      </DropdownItemsContainer>
    </DropdownContainer>
  )
}
