import React, { ChangeEventHandler } from 'react'
import { useDebounce } from 'use-debounce'
import DatePicker from 'react-datepicker'

import { DropdownComponent } from '../dropdown'
import {
  DatePickerContainer,
  FormInput,
  FormInputContainer,
  FormInputLabel,
} from './styles'
import { SearchFormContext } from '../../contexts/home'

export const InputForm: React.FC<{
  type: string
  name: string
  label: string
  icon: any
  value: any
  onChangeFunc: ChangeEventHandler<HTMLInputElement> | undefined
  dropdownType?: string
}> = ({ type, name, label, icon, value, onChangeFunc, dropdownType }) => {
  const [hasFocus, setHasFocus] = React.useState<boolean>(false)

  const [searchValueDebounced] = useDebounce(value, 500)

  const formContext = React.useContext(SearchFormContext)

  const renderInputComplement = () => {
    switch (dropdownType) {
      case 'cities':
        return searchValueDebounced ? (
          <DropdownComponent
            active={hasFocus}
            searchValue={searchValueDebounced}
            name={name}
          />
        ) : null
      case 'date':
        return (
          <DatePickerContainer>
            <DatePicker
              selected={formContext.searchForm.date}
              onChange={(date: Date) =>
                formContext.setSearchForm({
                  ...formContext.searchForm,
                  date: date,
                })
              }
            />
          </DatePickerContainer>
        )
      default:
        break
    }
  }

  return (
    <FormInputContainer>
      <FormInput
        type={type}
        name={name}
        id={name}
        hasValue={!!value}
        onFocus={() => setHasFocus(true)}
        onBlur={() => setHasFocus(false)}
        value={value}
        onChange={onChangeFunc}
      />
      <FormInputLabel>{label}</FormInputLabel>
      {icon}

      {renderInputComplement()}
    </FormInputContainer>
  )
}
