import React, { MouseEvent } from 'react'
import { FaAngleDown } from 'react-icons/fa'

import {
  DropdownButton,
  DropdownContainer,
  DropdownItem,
  DropdownItemsContainer,
  DropdownItemsList,
  DropdownItemText,
  DropdownTitle,
} from './style'

export const DropdownComponent: React.FC<{
  cities: Array<[string, number, number]>
}> = ({ cities }) => {
  const [dropdownOpen, setDropdownOpen] = React.useState<boolean>(false)

  const dropdownRef = React.useRef<HTMLDivElement>(null)

  const handleDropdownOpen = (): void => {
    setDropdownOpen((open) => !open)
  }

  const handleClickOutside = (event: EventListener): void => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setDropdownOpen((open) => !open)
    }
  }

  React.useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside)

    return () => {
      document.removeEventListener('mousedown', handleClickOutside)
    }
  }, [])

  return (
    <DropdownContainer ref={dropdownRef}>
      <DropdownButton onClick={handleDropdownOpen}>
        <DropdownTitle>Cities</DropdownTitle>
        <FaAngleDown size={16} color='#FFF' />
      </DropdownButton>

      <DropdownItemsContainer open={dropdownOpen}>
        <DropdownItemsList>
          {cities.map((city) => (
            <DropdownItem key={city[0]}>
              <DropdownItemText>{city[0]}</DropdownItemText>
            </DropdownItem>
          ))}
        </DropdownItemsList>
      </DropdownItemsContainer>
    </DropdownContainer>
  )
}
