import React from 'react'
import { DropdownItemCheckbox } from './styles'

export const CheckboxComponent: React.FC<{
  country: string
  handleSelectCountry: (country: string) => void
  handleRemoveCountry: (country: string) => void
}> = ({ country, handleSelectCountry, handleRemoveCountry }) => {
  const [isChecked, setIsChecked] = React.useState<boolean>(false)

  React.useEffect(() => {
    if (isChecked) {
      handleSelectCountry(country)
    } else {
      handleRemoveCountry(country)
    }
  }, [isChecked])

  return (
    <DropdownItemCheckbox
      id={country}
      checked={isChecked}
      onChange={() => setIsChecked((check) => !check)}
    />
  )
}
