import React from 'react'
import { FaMapMarkerAlt, FaCalendarDay, FaMale, FaPlus } from 'react-icons/fa'

import { InputForm } from '../../components/inputForm'
import {
  AddButton,
  AddButtonContainer,
  CardBody,
  CardContainer,
  CardForm,
  CardFormInputs,
  CardHeader,
  CardTitle,
  MoreInputContainer,
  SearchButton,
  SectionSearch,
} from './styles'

interface IIntermediateCity {
  fieldKey: number
  value: string
}
interface IForm {
  origin: string
  destination: string
  date: Date | undefined
  passengers: number
  intermediateCities: Array<IIntermediateCity>
}

export const Home: React.FC = () => {
  const [extraFields, setExtraFields] = React.useState<Array<number>>([])
  const [formData, setFormData] = React.useState<IForm>({
    origin: '',
    destination: '',
    date: undefined,
    passengers: 0,
    intermediateCities: [],
  })

  const handleAddNewFormField = (): void => {
    setExtraFields([...extraFields, extraFields.length + 1])
  }

  const handleFormDataChange = ({
    keyForm,
    idxForm,
    value,
  }: {
    keyForm: string
    idxForm: string
    value: string
  }): void => {
    if (keyForm !== 'intermediateCities') {
      setFormData({ ...formData, [keyForm]: value })
    } else {
      const newIntermediateCities = [
        ...formData.intermediateCities,
        { fieldKey: idxForm, value },
      ]
      setFormData({ ...formData, intermediateCities: newIntermediateCities })
    }
  }

  return (
    <SectionSearch>
      <CardContainer>
        <CardHeader>
          <CardTitle>Trip</CardTitle>
        </CardHeader>
        <CardBody>
          <CardForm>
            <CardFormInputs>
              <InputForm
                type='text'
                name='origin'
                label='City of origin'
                icon={<FaMapMarkerAlt size={24} />}
                value={formData.origin}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange('origin', e.target.value)
                }
              />

              <InputForm
                type='text'
                name='destiny'
                label='City of destination'
                icon={<FaMapMarkerAlt size={24} />}
                value={formData.destination}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange('destination', e.target.value)
                }
              />

              <InputForm
                type='text'
                name='trip-date'
                label='Date of the trip'
                icon={<FaCalendarDay size={24} />}
                value={formData.date}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange('date', e.target.value)
                }
              />

              <InputForm
                type='number'
                name='passengers'
                label='Number of passengers'
                icon={<FaMale size={24} />}
                value={formData.passengers}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange('passengers', e.target.value)
                }
              />
            </CardFormInputs>

            <MoreInputContainer>
              {extraFields.map((field) => (
                <InputForm
                  key={field}
                  type='text'
                  name={`intermediate-city-${field}`}
                  label='Intermediate city'
                  icon={<FaMapMarkerAlt size={24} />}
                  value={formData.intermediateCities[field]}
                  onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                    handleFormDataChange('intermediateCities', e.target.value)
                  }
                />
              ))}
            </MoreInputContainer>

            <AddButtonContainer>
              <AddButton type='button' onClick={handleAddNewFormField}>
                <FaPlus size={12} />
                Add intermediate city
              </AddButton>
            </AddButtonContainer>
            <SearchButton type='button'>Search</SearchButton>
          </CardForm>
        </CardBody>
      </CardContainer>
    </SectionSearch>
  )
}
