import React from 'react'

import {
  DropdownContainer,
  DropdownItem,
  DropdownItemsContainer,
  DropdownItemsList,
  DropdownItemText,
} from './style'

export const DropdownComponent: React.FC<{
  cities: Array<[string, number, number]>
  active: boolean
}> = ({ cities, active }) => {
  console.log('ðŸš€ ~ file: index.tsx ~ line 15 ~ active', active)
  const [dropdownOpen, setDropdownOpen] = React.useState<boolean>(active)

  const handleItemSelected = (): void => {
    setDropdownOpen(false)
  }

  return (
    <DropdownContainer>
      <DropdownItemsContainer open={active}>
        <DropdownItemsList>
          {cities.map((city) => (
            <DropdownItem key={city[0]} onClick={handleItemSelected}>
              <DropdownItemText>{city[0]}</DropdownItemText>
            </DropdownItem>
          ))}
        </DropdownItemsList>
      </DropdownItemsContainer>
    </DropdownContainer>
  )
}
