import React, { ChangeEventHandler } from 'react'
import { useDebounce } from 'use-debounce'

import { DropdownComponent } from '../dropdown'
import { FormInput, FormInputContainer, FormInputLabel } from './styles'

export const InputForm: React.FC<{
  type: string
  name: string
  label: string
  icon: any
  value: any
  onChangeFunc: ChangeEventHandler<HTMLInputElement> | undefined
  dropdownType?: string
}> = ({ type, name, label, icon, value, onChangeFunc, dropdownType }) => {
  const [hasFocus, setHasFocus] = React.useState<boolean>(false)

  const [searchValueDebounced] = useDebounce(value, 500)

  return (
    <FormInputContainer>
      <FormInput
        type={type}
        name={name}
        id={name}
        hasValue={!!value}
        onFocus={() => setHasFocus(true)}
        onBlur={() => setHasFocus(false)}
        value={value}
        onChange={onChangeFunc}
      />
      <FormInputLabel>{label}</FormInputLabel>
      {icon}

      {dropdownType === 'cities' && searchValueDebounced ? (
        <DropdownComponent
          active={hasFocus}
          searchValue={searchValueDebounced}
          name={name}
        />
      ) : null}
    </FormInputContainer>
  )
}
