import React from 'react'
import {
  FaMapMarkerAlt,
  FaCalendarDay,
  FaMale,
  FaPlus,
  FaTrash,
} from 'react-icons/fa'

import { InputForm } from '../../components/inputForm'
import {
  AddButton,
  AddButtonContainer,
  CardBody,
  CardContainer,
  CardForm,
  CardFormInputs,
  CardHeader,
  CardTitle,
  IntermediateCityContainer,
  MoreInputContainer,
  RemoveButton,
  SearchButton,
  SectionSearch,
} from './styles'

interface IIntermediateCity {
  fieldKey: number
  value: string
}
interface IForm {
  origin: string
  destination: string
  date: Date | undefined
  passengers: number | undefined
  intermediateCities: Map<number, string>
}

export const Home: React.FC = () => {
  const [extraFields, setExtraFields] = React.useState<Array<number>>([])
  const [extraFieldLastId, setExtraFieldLastId] = React.useState<number>(1)
  const [formData, setFormData] = React.useState<IForm>({
    origin: '',
    destination: '',
    date: undefined,
    passengers: undefined,
    intermediateCities: new Map(),
  })

  const handleAddNewFormField = (): void => {
    setExtraFields([...extraFields, extraFieldLastId])

    const newIntermediateCities = formData.intermediateCities.set(
      extraFieldLastId,
      ''
    )
    setFormData({ ...formData, intermediateCities: newIntermediateCities })
    setExtraFieldLastId((id) => ++id)
  }

  const handleFormDataChange = ({
    keyForm,
    extraFieldId,
    value,
  }: {
    keyForm: string
    extraFieldId?: number
    value: string
  }): void => {
    if (keyForm !== 'intermediateCities') {
      setFormData({ ...formData, [keyForm]: value })
    } else if (extraFieldId) {
      const newIntermediateCities = formData.intermediateCities.set(
        extraFieldId,
        value
      )
      setFormData({ ...formData, intermediateCities: newIntermediateCities })
    }
  }

  const handleRemoveExtraField = (extraFieldId: number) => {
    console.log(
      'ðŸš€ ~ file: index.tsx ~ line 82 ~ handleRemoveExtraField ~ extraFieldId',
      extraFieldId
    )
    setExtraFields((fields) => fields.splice(-1))

    formData.intermediateCities.delete(extraFieldId)
    // setFormData({ ...formData, intermediateCities: newIntermediateCities })
  }

  return (
    <SectionSearch>
      <CardContainer>
        <CardHeader>
          <CardTitle>Trip</CardTitle>
        </CardHeader>
        <CardBody>
          <CardForm>
            <CardFormInputs>
              <InputForm
                type='text'
                name='origin'
                label='City of origin'
                icon={<FaMapMarkerAlt size={24} />}
                value={formData.origin}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange({
                    keyForm: 'origin',
                    value: e.target.value,
                  })
                }
                dropdownType='cities'
              />

              <InputForm
                type='text'
                name='destiny'
                label='City of destination'
                icon={<FaMapMarkerAlt size={24} />}
                value={formData.destination}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange({
                    keyForm: 'destination',
                    value: e.target.value,
                  })
                }
                dropdownType='cities'
              />

              <InputForm
                type='text'
                name='trip-date'
                label='Date of the trip'
                icon={<FaCalendarDay size={24} />}
                value={formData.date}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange({
                    keyForm: 'date',
                    value: e.target.value,
                  })
                }
              />

              <InputForm
                type='number'
                name='passengers'
                label='Number of passengers'
                icon={<FaMale size={24} />}
                value={formData.passengers}
                onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                  handleFormDataChange({
                    keyForm: 'passengers',
                    value: e.target.value,
                  })
                }
              />
            </CardFormInputs>

            <MoreInputContainer>
              {extraFields.map((field) => (
                <IntermediateCityContainer key={field}>
                  <InputForm
                    type='text'
                    name={`intermediate-city-${field}`}
                    label='Intermediate city'
                    icon={<FaMapMarkerAlt size={24} />}
                    value={formData.intermediateCities.get(field)}
                    onChangeFunc={(e: React.ChangeEvent<HTMLInputElement>) =>
                      handleFormDataChange({
                        keyForm: 'intermediateCities',
                        extraFieldId: field,
                        value: e.target.value,
                      })
                    }
                    dropdownType='cities'
                  />

                  <RemoveButton onClick={() => handleRemoveExtraField(field)}>
                    <FaTrash size={18} />
                  </RemoveButton>
                </IntermediateCityContainer>
              ))}
            </MoreInputContainer>

            <AddButtonContainer>
              <AddButton type='button' onClick={handleAddNewFormField}>
                <FaPlus size={12} />
                Add intermediate city
              </AddButton>
            </AddButtonContainer>
            <SearchButton type='button'>Search</SearchButton>
          </CardForm>
        </CardBody>
      </CardContainer>
    </SectionSearch>
  )
}
