import React from 'react'
import { useQuery } from 'react-query'
import { fetchCities } from '../../api/cities'

import {
  DropdownContainer,
  DropdownItem,
  DropdownItemsContainer,
  DropdownItemsList,
  DropdownItemText,
} from './style'

export const DropdownComponent: React.FC<{
  active: boolean
  searchValue: string
}> = ({ active, searchValue }) => {
  const { isError, isSuccess, isLoading, data, error } = useQuery(
    ['cities'],
    fetchCities,
    { staleTime: 60000 }
  )

  const handleItemSelected = (city: string): void => {
    console.log('item selected...', city)
  }

  if (isLoading) {
    return (
      <DropdownContainer>
        <DropdownItemsContainer open={active}>
          <DropdownItemsList>
            <DropdownItem>
              <DropdownItemText>Loading items...</DropdownItemText>
            </DropdownItem>
          </DropdownItemsList>
        </DropdownItemsContainer>
      </DropdownContainer>
    )
  }

  if (isError) {
    return (
      <DropdownContainer>
        <DropdownItemsContainer open={active}>
          <DropdownItemsList>
            <DropdownItem>
              <DropdownItemText>
                Something went wrong, try again.
              </DropdownItemText>
            </DropdownItem>
          </DropdownItemsList>
        </DropdownItemsContainer>
      </DropdownContainer>
    )
  }

  return (
    <DropdownContainer>
      <DropdownItemsContainer open={active}>
        <DropdownItemsList>
          {data?.cities ? (
            data.cities.france?.map((city: any) => (
              <DropdownItem
                key={city[0]}
                onClick={() => handleItemSelected(city[0])}
              >
                <DropdownItemText>{city[0]}</DropdownItemText>
              </DropdownItem>
            ))
          ) : (
            <DropdownItem>
              <DropdownItemText>No results found.</DropdownItemText>
            </DropdownItem>
          )}
        </DropdownItemsList>
      </DropdownItemsContainer>
    </DropdownContainer>
  )
}
